(function(){$("#TodoItemAddButton").click(function(n){n.preventDefault();$.ajax({url:"/api/todos",type:"POST",data:{text:$("#TodoItemText").val()}}).then(function(n){var t=$("<li />").attr("data-id",n.id).appendTo("#TodoList");$('<i class="fa fa-check"><\/i>').appendTo(t);$('<span class="todo-item-text"><\/span>').text(n.text).appendTo(t);$('<a href="#" class="todo-item-delete-link"><i class="fa fa-times"><\/i><\/a>').appendTo(t);$("#TodoItemText").val("")})});$("#TodoList").on("click",".todo-item-delete-link",function(n){n.preventDefault();var t=$(this).closest("li");$.ajax({url:"/api/todos/"+t.attr("data-id"),type:"DELETE"}).then(function(){t.remove()})})})();